func extractNames(filename string) []string {
  dat, err := ioutil.ReadFile(filename)

  if err != nil {
    panic(err)
  }

  return strings.Split(strings.Replace(string(dat), "\"", "", -1), ",")
}

func getRandomName(names []string) string {
  seed := rand.NewSource(time.Now().UnixNano())
  index := rand.New(seed).Intn(len(names))

  name := strings.TrimSpace(names[index])

  return name
}

func GenerateName() string {
  name := getRandomName(firstNames) + " " + getRandomName(lastNames)

  return name
}